(function(){
    "use strict";

    angular.module('app.controllers').controller('IndexcreatorCtrl', function($scope,$rootScope,DialogService,DataService, $timeout,$state, $filter, leafletData, toastr, VectorlayerService){
        //
        var vm = this;
        vm.map = null;
        vm.data = [];
        vm.toSelect = [];
        vm.selected = [];
        vm.selectedRows = [];
        vm.selectedResources =[];
        vm.sortedResources = [];
        vm.iso_errors = 0;
        vm.iso_checked = false;
        vm.selectedIndex = 0;
        vm.step = 0;
        vm.search = search;
        vm.deleteData = deleteData;
        vm.deleteSelected = deleteSelected;
        vm.onOrderChange = onOrderChange;
        vm.onPaginationChange = onPaginationChange;
        vm.checkForErrors = checkForErrors;
        vm.clearErrors = clearErrors;
        vm.showUploadContainer = false;
        vm.openClose = openClose;
        vm.checkColumnData = checkColumnData;
        vm.editColumnData = editColumnData;
        vm.fetchIso = fetchIso;
        vm.editRow = editRow;
        vm.saveData = saveData;
        vm.listResources = listResources;
        vm.toggleListResources = toggleListResources;
        vm.selectedResource = selectedResource;
        vm.toggleResource = toggleResource;
        vm.increasePercentage = increasePercentage;
        vm.decreasePercentage = decreasePercentage;

        vm.meta = {
          iso_field: '',
          country_field:'',
          table:[]
        };
        vm.query = {
          filter: '',
          order: '-errors',
          limit: 15,
          page: 1
        };
        vm.treeOptions = {
          dropped:function(event){
            calcPercentage(vm.sortedResources);
          }
        };

        //Run Startup-Funcitons
        activate();

        function activate(){
          clearMap();
        }
        function openClose(active){
          return active ? 'remove' : 'add';
        }
        function clearLayerStyle(feature){
      			var style = {
              color:'rgba(255,255,255,0)',
              outline: {
    						color: 'rgba(255,255,255,0)',
    						size: 1
    					}
            };
      			return style;
        }
        function clearMap(){
          	leafletData.getMap('map').then(function (map) {
              vm.mvtSource = VectorlayerService.getLayer();
              $timeout(function(){
                vm.mvtSource.setStyle(clearLayerStyle);
              })
            });
        }
        function search(predicate) {
          vm.filter = predicate;
        };
        function onOrderChange(order) {
          return vm.data = $filter('orderBy')(vm.data, [order], true)
        };
        function onPaginationChange(page, limit) {
          //console.log(page, limit);
          //return $nutrition.desserts.get($scope.query, success).$promise;
        };
        function checkForErrors(item){
          return item.errors.length > 0 ? 'md-warn': '';
        }
        function clearErrors(){
          angular.forEach(vm.data, function(row, key){
            angular.forEach(row.data[0], function(item, k){
              if(isNaN(item) || item < 0){
                if(item == "NA" || item < 0 || item.indexOf('#N/A') > -1){
                  vm.data[key].data[0][k] = '';
                  vm.errors --;
                  row.errors.splice(0,1);
                }
              }
              switch (item) {
                case 'Cabo Verde':
                      vm.data[key].data[0][k]   = 'Cape Verde';
                  break;
                case "Democratic Peoples Republic of Korea":
                        vm.data[key].data[0][k]   = "Democratic People's Republic of Korea";
                    break;
                case "Cote d'Ivoire":
                        vm.data[key].data[0][k]   = "Ivory Coast";
                    break;
                case "Lao Peoples Democratic Republic":
                        vm.data[key].data[0][k]   = "Lao People's Democratic Republic";
                    break;
                default:
                  break;
              }
            });
            if(!row.data[0][vm.meta.iso_field]){
              vm.iso_errors++;
              vm.errors++
              row.errors.push({
                type:"2",
                message:"Iso field is not valid!",
                column: vm.meta.iso_field
              })
            }
          });
        }
        function checkColumnData(key){
          if(typeof vm.meta.table[key] != "undefined"){
            if(vm.meta.table[key].title){
              return true;
            }
          }
          return false;
        }
        function editColumnData(e, key){
          vm.toEdit = key;
          DialogService.fromTemplate('editcolumn', $scope);
        }
        function deleteSelected(){
          angular.forEach(vm.selected, function(item, key){
            angular.forEach(item.errors, function(error, k){
              if(error.type == 2){
                vm.iso_errors --;
              }
              vm.errors --;
            })
            vm.data.splice(vm.data.indexOf(item), 1);
            vm.selected.splice(key, 1);
          });
          if(vm.data.length == 0){
            vm.deleteData();
            $state.got('app.index.create');
          }
        }
        function editRow(){
          vm.row = vm.selected[0];
          DialogService.fromTemplate('editrow', $scope);
        }
        function deleteData(){
          vm.data = [];
        }
        function fetchIso(){
          if(!vm.meta.iso_field){
            toastr.error('Check your selection for the ISO field', 'Column not specified!');
            return false;
          }
          if(!vm.meta.country_field){
            toastr.error('Check your selection for the COUNTRY field', 'Column not specified!');
            return false;
          }
          if(vm.meta.country_field == vm.meta.iso_field){
            toastr.error('ISO field and COUNTRY field can not be the same', 'Selection error!');
            return false;
          }

          vm.toSelect = [];
          vm.notFound = [];
          var entries = [];
          angular.forEach(vm.data, function(item, key){
              entries.push({
                iso: item.data[0][vm.meta.iso_field],
                name: item.data[0][vm.meta.country_field]
              });
          });
          DataService.post('/nations/byIsoNames', {data:entries}).then(function(response){
              angular.forEach(response.data, function(country, key){
                angular.forEach(vm.data, function(item, k){
                    if(country.name == item.data[0][vm.meta.country_field]){
                      if(country.data.length > 1){
                        var toSelect = {
                          entry: item,
                          options: country.data
                        };
                        vm.toSelect.push(toSelect);
                      }
                      else{
                        if(typeof country.data[0] != "undefined"){
                          vm.data[k].data[0][vm.meta.iso_field] = country.data[0].iso;
                          vm.data[k].data[0][vm.meta.country_field] = country.data[0].admin;
                          if(item.errors.length){
                            angular.forEach(item.errors, function(error, e){
                              if(error.type == 2){
                                vm.iso_errors --;
                                vm.errors --;
                                item.errors.splice(e, 1);
                              }
                            })
                          }
                        }
                        else{
                          console.log(country);
                        }
                      }
                    }
                });
              });
              if(vm.toSelect.length){
                DialogService.fromTemplate('selectisofetchers', $scope);
              }
              vm.iso_checked = true;
          }, function(response){
            console.log(response);
            toastr.error('Please check your field selections', response.data.message);
          })
        }
        function saveData(){
          var insertData = {data:[]};
          var meta = [], fields = [];
          angular.forEach(vm.data, function(item, key){
            if(item.errors.length == 0){
              item.data[0].year = vm.meta.year;
              insertData.data.push(item.data[0]);
            }
          });
          angular.forEach(vm.data[0].data[0], function(item, key){
            console.log(vm.meta.table[key], key);
            if(vm.meta.table[key]){
              var field = {
                'column': key,
                'title':vm.meta.table[key].title,
                'description':vm.meta.table[key].description
              };
              fields.push(field);
            }
          });
          angular.forEach(vm.data.table, function(item, key){
            meta.push({
              field:key,
              data: item
            })
          })
          vm.meta.fields = fields;
          vm.meta.info = meta;
          console.log(vm.meta);
          DataService.post('data/tables', vm.meta).then(function(response){
              DataService.post('data/tables/'+response.data.table_name+'/insert', insertData).then(function(res){
                if(res.data == true){
                  toastr.success(insertData.data.length+' items importet to '+vm.meta.name,'Success');
                  vm.data = [];
                  vm.step = 0;
                }
              });
          }, function(response){
            if(response.message){
              toastr.error(response.message, 'Ouch!');
            }
          })
        }
        function toggleListResources(){
          vm.showResources = !vm.showResources;
          if(vm.showResources){
            vm.listResources();
          }
        }
        function listResources(){

          if(!vm.resources){
            DataService.getAll('data/tables').then(function(response){
              vm.resources = response;
              vm.selectedResources = [], vm.sortedResources = [];
            })
          }

        }
        function selectedResource(resource){
          return vm.selectedResources.indexOf(resource) > -1 ? true : false;
        }
        function toggleResource(resource){
          if(vm.selectedResources.indexOf(resource) > -1){
            vm.selectedResources.splice(vm.selectedResources.indexOf(resource), 1);
          }
          else{
            vm.selectedResources.push(resource);
          }
          angular.copy(vm.selectedResources, vm.sortedResources);
          angular.forEach(vm.sortedResources, function(res, key){
             vm.sortedResources[key].weight = parseInt(100 / vm.sortedResources.length);
             vm.sortedResources[key].nodes = [];
          });
          calcPercentage(vm.sortedResources);
        }
        function calcPercentage(nodes){
          angular.forEach(nodes, function(node, key){
            nodes[key].weight = parseInt(100 / nodes.length);
            calcPercentage(nodes.node);
          });
        }
        function increasePercentage(item){
          console.log(item);
        }
        function decreasePercentage(item){
          console.log(item)
        }
        $scope.$on("$stateChangeStart", function (event, toState, toParams, fromState, fromParams) {
          switch (toState.name) {
            case 'app.index.create.check':
              break;
            case 'app.index.create.meta':
                if(!vm.meta.iso_field){
                  toastr.error('No field for ISO Code selected!', 'Error');
                  event.preventDefault();
                   $rootScope.stateIsLoading = false;
                }
                break;
            case 'app.index.create.final':
              break;
            default:
                if(vm.data.length){
                  $scope.toState = toState;
                  DialogService.fromTemplate('loosedata', $scope, vm.deleteData);
                  event.preventDefault();
                  $rootScope.stateIsLoading = false;
                }
              break;
          }
        });
        $scope.$on("$stateChangeSuccess", function (event, toState, toParams, fromState, fromParams) {
          if(!vm.data.length){
            $state.go('app.index.create');
          }
          else{
            switch (toState.name) {
              case 'app.index.create':
                  vm.step = 0;
                break;
              case 'app.index.create.check':
                  vm.step = 1;
                  vm.showUploadContainer = false;
                break;
              case 'app.index.create.meta':
                  vm.step = 2;
                  break;
              case 'app.index.create.final':
                  vm.step = 3;
                  break;
              default:
                break;
            }
          }
        });
    });

})();
