(function () {
	"use strict";

	angular.module('app.controllers').controller('IndexeditorcategoryCtrl', function ($state, category, categories, DataService,ContentService) {
    var vm = this;
    vm.category = category;
		vm.categories = categories;
		vm.options = {
			globalSave:true,
			postDone:function(data){
				$state.go('app.index.editor.categories.category', {id:data.id})
			},
		}
  });
})();
