(function () {
	"use strict";

	angular.module('app.controllers').controller('IndexeditorCtrl', function ($filter,DataService) {
		//
		var vm = this;

		vm.dataproviders = [];
		vm.selectedItem = null;
		vm.searchText = null;
		vm.querySearch = querySearch;

    activate();

    function activate(){
      loadAll();
    }
		function querySearch(query) {
      console.log($filter('findbyname')(vm.dataproviders, query, 'title'));
			var results = query ? $filter('findbyname')(vm.dataproviders, query, 'title') : [];
      console.log(results);
			return results;
		}
		function loadAll() {
      DataService.getAll('dataproviders').then(function(data){
        vm.dataproviders = data;
      });
		}
	});

})();
